mapper.select:
select qna_no, qna_writer, qna_title, qna_wdate, qna_contents, qna_viewCount, contest_id
from contest_qna
where qna_no=#{qna_no}

mapper.update:
  	update contest_qna 
  	set qna_title=#{qna_title}, qna_wdate=now(), qna_contents=#{qna_contents}
		where qna_no=#{qna_no}

mapper.select:
select count(*)
from contest_qna
where contest_id = #{contest_id}

mapper.update:
update contest_qna 
set qna_replyCount = qna_replyCount + #{amount} 
where qna_no = #{qna_no}

update contest_qna 
set qna_viewCount = qna_viewCount + 1 
where qna_no=#{qna_no}
